/* Basic Grid Setup */
.grid-container {
  display: grid;
  /* Children become grid items */
}

/* Simple Grid - 3 columns */
.three-column-grid {
  display: grid;
  grid-template-columns: 1fr 1fr 1fr;
  /* Or shorthand: */
  grid-template-columns: repeat(3, 1fr);
  gap: 20px;
}

/* Grid with different column sizes */
.mixed-grid {
  display: grid;
  grid-template-columns: 200px 1fr 1fr;
  /* Fixed 200px, then two flexible columns */
  gap: 20px;
}

/* Rows and columns */
.full-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-template-rows: 100px auto 100px;
  gap: 20px;
}

/* Auto-fill responsive grid */
.responsive-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 20px;
  /* Automatically creates columns that fit */
}

/* Flexbox vs Grid comparison */
/* Flexbox: One-dimensional (row OR column) */
.flex-layout {
  display: flex;
  flex-wrap: wrap;
}

/* Grid: Two-dimensional (rows AND columns) */
.grid-layout {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
}

/* Fixed size columns */
.fixed-grid {
  display: grid;
  grid-template-columns: 200px 200px 200px;
  grid-template-rows: 100px 100px;
}

/* Flexible fr units */
.flexible-grid {
  display: grid;
  grid-template-columns: 1fr 2fr 1fr;
  /* Left: 25%, Center: 50%, Right: 25% */
}

/* repeat() function */
.repeat-grid {
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  /* Same as: 1fr 1fr 1fr 1fr */
}

/* minmax() for constraints */
.minmax-grid {
  display: grid;
  grid-template-columns: minmax(200px, 1fr) minmax(300px, 2fr);
  /* Min 200px, max 1fr | Min 300px, max 2fr */
}

/* auto-fill responsive grid */
.auto-fill {
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  /* Creates as many columns as fit */
}

/* auto-fit responsive grid */
.auto-fit {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  /* Stretches items to fill space */
}

/* Mixed units */
.mixed-units {
  display: grid;
  grid-template-columns: 200px 1fr 2fr 20%;
  grid-template-rows: auto 200px 1fr;
}

/* Implicit vs Explicit rows */
.implicit-rows {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  /* Rows created automatically */
  grid-auto-rows: 150px; /* Height of auto-generated rows */
}

/* Real-world layout */
.dashboard {
  display: grid;
  grid-template-columns: 250px 1fr;
  grid-template-rows: 60px 1fr 50px;
  min-height: 100vh;
  /* Sidebar | Main Content */
  /* Header | Content | Footer */
}

