}

/* Container alignment - all items */
.grid-container {
  display: grid;
  grid-template-columns: repeat(3, 200px);
  grid-template-rows: repeat(3, 200px);

  justify-items: center; /* Horizontal alignment */
  align-items: center;   /* Vertical alignment */
  /* Or shorthand: */
  place-items: center; /* Both axes */
  gap: 20px;
}

/* Individual item alignment */
.item-1 {
  justify-self: start; /* Left align this item */
  align-self: end;     /* Bottom align this item */
}

.item-2 {
  place-self: center; /* Center this item */
}

/* Spanning multiple columns/rows */
.span-columns {
  grid-column: 1 / 3; /* Span from line 1 to line 3 */
  /* Or using span: */
  grid-column: span 2; /* Span 2 columns */
}

.span-rows {
  grid-row: 1 / 4; /* Span from row 1 to row 4 */
  /* Or: */
  grid-row: span 3; /* Span 3 rows */
}

/* Both at once */
.large-item {
  grid-column: 1 / 3; /* Columns 1-3 */
  grid-row: 1 / 3;    /* Rows 1-3 */
  /* Or: */
  grid-area: 1 / 1 / 3 / 3;
