}

/* Explicit positioning */
.positioned-item {
  grid-column-start: 2;
  grid-column-end: 4;
  grid-row-start: 1;
  grid-row-end: 2;
}

/* Negative line numbers (from end) */
.last-column {
  grid-column: -2 / -1; /* Second to last column */
}

