  --primary: #007bff;
  --text: #333;
  --bg: #fff;
}

body {
  font-family: 'Poppins', sans-serif;
  color: var(--text);
  background: var(--bg);
  margin: 0;
}

header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 20px;
}

.hero {
  text-align: center;
  padding: 80px 20px;
}

.features {
