// Starting local MongoDB server
mongod --dbpath /data/db

// Connecting using Mongo shell
mongo

// MongoDB Atlas connection string
mongodb+srv://username:password@cluster0.mongodb.net/mydb

// Creating a database and collection
use myDatabase
db.createCollection('users')

// Inserting a document
db.users.insertOne({ name: 'Alice', age: 25 })

// Insert
 db.users.insertOne({ name: 'Bob', age: 30 })

// Find
 db.users.find({ age: { $gt: 20 } })

// Update
 db.users.updateOne({ name: 'Bob' }, { $set: { age: 31 } })

// Delete
 db.users.deleteOne({ name: 'Bob' })

// Find users older than 25
 db.users.find({ age: { $gt: 25 } })

// Find users named 'Alice' or 'Bob'
 db.users.find({ $or: [{ name: 'Alice' }, { name: 'Bob' }] })

// Sort by age descending and limit 3
 db.users.find().sort({ age: -1 }).limit(3)

// Only return name and age fields
 db.users.find({}, { name: 1, age: 1, _id: 0 })

const mongoose = require('mongoose');

mongoose.connect('mongodb://localhost/mydb')
  .then(() => console.log('MongoDB connected'))
  .catch(err => console.error(err));

const userSchema = new mongoose.Schema({
  name: String,
  age: Number,
  email: { type: String, required: true }
});

const User = mongoose.model('User', userSchema);

// Create
const user = await User.create({ name: 'Alice', age: 25 });

// Read
const users = await User.find({ age: { $gt: 20 } });

// Update
await User.updateOne({ name: 'Alice' }, { $set: { age: 26 } });

// Delete
await User.deleteOne({ name: 'Alice' });

// Embedding example
const userSchema = new mongoose.Schema({
  name: String,
  posts: [{ title: String, content: String }]
});

// Referencing example
const postSchema = new mongoose.Schema({
  title: String,
  author: { type: mongoose.Schema.Types.ObjectId, ref: 'User' }
});

// Total orders per customer
 db.orders.aggregate([
  { $match: { status: 'completed' } },
  { $group: { _id: '$customerId', totalOrders: { $sum: 1 } } },
  { $sort: { totalOrders: -1 } }
]);

// Create an index on 'name'
 db.users.createIndex({ name: 1 })

// Create a compound index on 'name' and 'age'
 db.users.createIndex({ name: 1, age: -1 })

// .env file
MONGO_URI=mongodb://localhost:27017/mydb

// app.js
const mongoose = require('mongoose');
require('dotenv').config();

mongoose.connect(process.env.MONGO_URI, { useNewUrlParser: true, useUnifiedTopology: true })
  .then(() => console.log('MongoDB connected'))
  .catch(err => console.error(err));

// Express CRUD example
const express = require('express');
const User = require('./models/User');
const app = express();
app.use(express.json());

// Create
app.post('/users', async (req, res) => {
  const user = await User.create(req.body);
  res.json(user);
});

// Read
app.get('/users', async (req, res) => {
  const users = await User.find();
  res.json(users);
});
